import React, { useState, useEffect } from "react";

const People = () => {
  const [people, setPeople] = useState([]); //whenever state updates it forces a rerender

  async function howManyPeopleAreInSpace() {
    try {
      const response = await fetch("http://api.open-notify.org/astros.json");
      const data = await response.json();
      setPeople(data.people);
    } catch (err) {
      console.log(err);
    }
  }

  useEffect(() => {
    howManyPeopleAreInSpace();
  }, []);

  return (
    <div>
      <h1 className="header">People in Space</h1>
      <div className="people-main-container">
        {people.length
          ? people.map((e, idx) => {
              return (
                <div key={`${idx}: ${e.name}`} className="card">
                  <p>
                    <span className="makeBold">Person: {e.name}</span>
                  </p>
                  <p>
                    <span className="makeBold">Craft: {e.craft}</span>
                  </p>
                </div>
              );
            })
          : null}
      </div>
    </div>
  );
};

export default People;
